mapper.select:
select
  id, role_name, descpt, code, insert_uid, insert_time, update_time
from shiro1.role


        and id = #{id}


        and role_name = #{roleName}


        and descpt = #{descpt}


        and code = #{code}


        and insert_uid = #{insertUid}


        and insert_time = #{insertTime}


        and update_time = #{updateTime}
